Item.method_code:
// CMActions_SetECR
// Called OnComplete from the CMActions_AddToECR action. Sets the ChangeItem attribute and calls
//  CMActions_AddChangeItem to add the ECR
if (inArgs && inArgs.results && inArgs.results.getItemByIndex(0) &&
    inArgs.results.getItemByIndex(0).getAttribute("ChangeItem","").length > 0)
{
  inArgs.results.getItemByIndex(0).setAttribute("ChangeItem","ECR");
  top.aras.evalMethod("CMActions_AddChangeItem",inArgs.results.dom.xml);
}
