Item.method_code:
// CMActions_SetPR
// Called OnComplete from the CMActions_AddToPR action. Sets the ChangeItem attribute and calls
//  CMActions_AddChangeItem to add the PR
if (inArgs && inArgs.results && inArgs.results.getItemByIndex(0) &&
    inArgs.results.getItemByIndex(0).getAttribute("ChangeItem","").length > 0)
{
  inArgs.results.getItemByIndex(0).setAttribute("ChangeItem","PR");
  top.aras.evalMethod("CMActions_AddChangeItem",inArgs.results.dom.xml);
}
