Item.method_code:
// CMActions_SetECN
// Called OnComplete from the CMActions_AddToECN action. Sets the ChangeItem attribute and calls
//  CMActions_AddChangeItem to add the ECN
if (inArgs && inArgs.results && inArgs.results.getItemByIndex(0) &&
    inArgs.results.getItemByIndex(0).getAttribute("ChangeItem","").length > 0)
{
  inArgs.results.getItemByIndex(0).setAttribute("ChangeItem","ECN");
  top.aras.evalMethod("CMActions_AddChangeItem",inArgs.results.dom.xml);
}
