Item.method_code:
// CMActions_SimpleECO
// Called OnComplete from the CMActions_AddToSimpleECO action. Sets the ChangeItem attribute and calls
//  CMActions_AddChangeItem to add the ECO
if (inArgs && inArgs.results && inArgs.results.getItemByIndex(0) &&
    inArgs.results.getItemByIndex(0).getAttribute("ChangeItem","").length > 0)
{
  inArgs.results.getItemByIndex(0).setAttribute("ChangeItem","Simple ECO");
  top.aras.evalMethod("CMActions_AddChangeItem",inArgs.results.dom.xml);
}
