Item.method_code:
// CMActions_SimpleMCO
// Called OnComplete from the CMActions_AddToSimpleMCO action. Sets the ChangeItem attribute and calls
//  CMActions_AddChangeItem to add the MCO
if (inArgs && inArgs.results && inArgs.results.getItemByIndex(0) &&
    inArgs.results.getItemByIndex(0).getAttribute("ChangeItem","").length > 0)
{
  inArgs.results.getItemByIndex(0).setAttribute("ChangeItem","Simple MCO");
  top.aras.evalMethod("CMActions_AddChangeItem",inArgs.results.dom.xml);
}
